import React, { useState, useEffect } from "react";
import axios from "axios";
import UploadBox from "./components/UploadBox";

const BACKEND = "http://127.0.0.1:8000";

export default function App() {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, [darkMode]);

  const handleUpload = async (file) => {
    setError("");
    setLoading(true);
    setSummary(null);

    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post(`${BACKEND}/upload`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const data = res.data;
      if (data.download_url && data.download_url.startsWith("/")) {
        data.download_url = BACKEND + data.download_url;
      }
      setSummary(data);
    } catch (err) {
      console.error(err);
      setError(err?.response?.data?.detail || "Error processing file.");
    } finally {
      setLoading(false);
    }
  };

  // ========================================
  // 📊 Dynamic Data Insights Generator
  // ========================================
  const generateInsights = (rows) => {
    if (!rows || rows.length === 0) return [];
    const columns = Object.keys(rows[0]);
    const insights = [];

    columns.forEach((col) => {
      const values = rows.map((r) => r[col]).filter((v) => v !== null && v !== undefined && v !== "");

      if (values.length === 0) return;

      // Check if numeric
      const numericValues = values.map((v) => parseFloat(v)).filter((v) => !isNaN(v));
      if (numericValues.length >= values.length * 0.7) {
        const avg = (numericValues.reduce((a, b) => a + b, 0) / numericValues.length).toFixed(2);
        insights.push(`Average ${col}: ${avg}`);
      } else {
        // Categorical insight — find most frequent
        const freq = {};
        values.forEach((v) => {
          const val = String(v).trim();
          freq[val] = (freq[val] || 0) + 1;
        });
        const sorted = Object.entries(freq).sort((a, b) => b[1] - a[1]);
        if (sorted.length > 0) {
          insights.push(`Most common ${col}: ${sorted[0][0]} (${sorted[0][1]} times)`);
        }
      }
    });

    // Return top 3 insights for simplicity
    return insights.slice(0, 3);
  };

  const insights = summary?.sample_data ? generateInsights(summary.sample_data) : [];

  return (
    <div
      style={{
        maxWidth: 900,
        margin: "40px auto",
        padding: "20px",
        fontFamily: "Arial",
        transition: "background 0.3s, color 0.3s",
      }}
      className={darkMode ? "dark" : ""}
    >
      {/* 🌙 Dark Mode Toggle */}
      <div style={{ textAlign: "right", marginBottom: 10 }}>
        <button
          onClick={() => setDarkMode(!darkMode)}
          style={{
            padding: "6px 12px",
            background: darkMode ? "#f0f0f0" : "#333",
            color: darkMode ? "#000" : "#fff",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          {darkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
        </button>
      </div>

      <h1>📊 DataClean Analyzer</h1>
      <p>Upload any CSV or Excel file — we’ll clean, analyze, and summarize it automatically.</p>

      <UploadBox onUpload={handleUpload} loading={loading} />

      {/* Loader */}
      {loading && (
        <div style={{ textAlign: "center", marginTop: 20 }}>
          <div
            style={{
              width: 40,
              height: 40,
              border: "5px solid #ccc",
              borderTop: "5px solid #007bff",
              borderRadius: "50%",
              animation: "spin 1s linear infinite",
              margin: "0 auto",
            }}
          ></div>
          <p style={{ color: "#888", marginTop: 10 }}>Processing file... please wait.</p>
        </div>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}

      {summary && (
        <div style={{ marginTop: 20 }}>
          <h2>📋 Summary</h2>
          <ul>
            <li><b>Total Rows:</b> {summary.total_rows ?? "N/A"}</li>
            <li><b>Cleaned Rows:</b> {summary.cleaned_rows ?? "N/A"}</li>
            <li><b>Missing Values:</b> {summary.missing_values ?? "N/A"}</li>
            <li><b>Rows Removed:</b> {summary.rows_removed_percent ?? "N/A"}</li>
          </ul>

          {/* 💡 Data Insights */}
          {insights.length > 0 && (
            <div
              style={{
                marginTop: 20,
                padding: 15,
                borderRadius: 8,
                background: darkMode ? "#222" : "#f9f9f9",
              }}
            >
              <h3>💡 Data Insights</h3>
              <ul>
                {insights.map((i, idx) => (
                  <li key={idx}>{i}</li>
                ))}
              </ul>
            </div>
          )}

          {/* Table Preview */}
          <h3 style={{ marginTop: 20 }}>📈 Data Preview</h3>
          <div style={{ overflowX: "auto" }}>
            <table
              border="1"
              cellPadding="6"
              cellSpacing="0"
              style={{
                width: "100%",
                borderCollapse: "collapse",
                background: darkMode ? "#333" : "white",
                color: darkMode ? "#eee" : "#000",
              }}
            >
              {Array.isArray(summary.columns) && Array.isArray(summary.sample_data) ? (
                <>
                  <thead>
                    <tr>
                      {summary.columns.map((c) => (
                        <th key={c}>{c}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {summary.sample_data.map((row, i) => (
                      <tr key={i}>
                        {summary.columns.map((c) => (
                          <td key={c}>{row[c] ?? ""}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </>
              ) : (
                <tbody>
                  <tr>
                    <td colSpan="100%">No data to display</td>
                  </tr>
                </tbody>
              )}
            </table>
          </div>

          {/* Download Button */}
          {summary.download_url && (
            <div style={{ marginTop: 15 }}>
              <a href={summary.download_url} target="_blank" rel="noreferrer">
                <button
                  style={{
                    padding: "8px 12px",
                    cursor: "pointer",
                    background: darkMode ? "#007bff" : "#4CAF50",
                    color: "white",
                    border: "none",
                    borderRadius: 6,
                  }}
                >
                  Download Cleaned CSV
                </button>
              </a>
            </div>
          )}
        </div>
      )}

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        body.dark-mode {
          background-color: #121212;
          color: #eee;
        }
      `}</style>
    </div>
  );
}
