import React, { useState, useEffect } from "react";
import axios from "axios";
import UploadBox from "./components/UploadBox";

const BACKEND = "http://127.0.0.1:8000";

export default function App() {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, [darkMode]);

  const handleUpload = async (file) => {
    setError("");
    setLoading(true);
    setSummary(null);

    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post(`${BACKEND}/upload`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const data = res.data;
      if (data.download_url && data.download_url.startsWith("/")) {
        data.download_url = BACKEND + data.download_url;
      }
      setSummary(data);
    } catch (err) {
      console.error(err);
      setError(err?.response?.data?.detail || "Error processing file.");
    } finally {
      setLoading(false);
    }
  };

  const generateInsights = (rows) => {
    if (!rows || rows.length === 0) return [];
    const columns = Object.keys(rows[0]);
    const insights = [];

    columns.forEach((col) => {
      const values = rows.map((r) => r[col]).filter((v) => v !== null && v !== undefined && v !== "");
      if (values.length === 0) return;

      const numericValues = values.map((v) => parseFloat(v)).filter((v) => !isNaN(v));
      if (numericValues.length >= values.length * 0.7) {
        const avg = (numericValues.reduce((a, b) => a + b, 0) / numericValues.length).toFixed(2);
        insights.push(`Average ${col}: ${avg}`);
      } else {
        const freq = {};
        values.forEach((v) => {
          const val = String(v).trim();
          freq[val] = (freq[val] || 0) + 1;
        });
        const sorted = Object.entries(freq).sort((a, b) => b[1] - a[1]);
        if (sorted.length > 0) {
          insights.push(`Most common ${col}: ${sorted[0][0]} (${sorted[0][1]} times)`);
        }
      }
    });

    return insights.slice(0, 3);
  };

  const insights = summary?.sample_data ? generateInsights(summary.sample_data) : [];

  return (
    <div className={`app-container ${darkMode ? "dark" : ""}`}>
      <div style={{ textAlign: "right", marginBottom: 10 }}>
        <button
          onClick={() => setDarkMode(!darkMode)}
          style={{
            padding: "6px 12px",
            background: darkMode ? "#f0f0f0" : "#333",
            color: darkMode ? "#000" : "#fff",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
        </button>
      </div>

      <h1>üìä DataClean Analyzer</h1>
      <p>Upload any CSV or Excel file ‚Äî we‚Äôll clean, analyze, and summarize it automatically.</p>

      <UploadBox onUpload={handleUpload} loading={loading} />

      {loading && (
        <div style={{ textAlign: "center", marginTop: 20 }}>
          <div className="loader"></div>
          <p style={{ color: "#888", marginTop: 10 }}>Processing file... please wait.</p>
        </div>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}

      {summary && (
        <div style={{ marginTop: 20 }}>
          <h2>üìã Summary</h2>
          <ul>
            <li><b>Total Rows:</b> {summary.total_rows ?? "N/A"}</li>
            <li><b>Cleaned Rows:</b> {summary.cleaned_rows ?? "N/A"}</li>
            <li><b>Missing Values:</b> {summary.missing_values ?? "N/A"}</li>
            <li><b>Rows Removed:</b> {summary.rows_removed_percent ?? "N/A"}</li>
          </ul>

          {insights.length > 0 && (
            <div className="insights-box">
              <h3>üí° Data Insights</h3>
              <ul>
                {insights.map((i, idx) => (
                  <li key={idx}>{i}</li>
                ))}
              </ul>
            </div>
          )}

          <h3 style={{ marginTop: 20 }}>üìà Data Preview</h3>
          <div style={{ overflowX: "auto" }}>
            <table className="data-table" border="1" cellPadding="6" cellSpacing="0">
              {Array.isArray(summary.columns) && Array.isArray(summary.sample_data) ? (
                <>
                  <thead>
                    <tr>
                      {summary.columns.map((c) => (
                        <th key={c}>{c}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {summary.sample_data.map((row, i) => (
                      <tr key={i}>
                        {summary.columns.map((c) => (
                          <td key={c}>{row[c] ?? ""}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </>
              ) : (
                <tbody>
                  <tr>
                    <td colSpan="100%">No data to display</td>
                  </tr>
                </tbody>
              )}
            </table>
          </div>

          {summary.download_url && (
            <div style={{ marginTop: 15 }}>
              <a href={summary.download_url} target="_blank" rel="noreferrer">
                <button className="download-btn">Download Cleaned CSV</button>
              </a>
            </div>
          )}
        </div>
      )}

      <style>{`
        body.dark-mode, .app-container.dark {
          background-color: #121212 !important;

          min-height: 100vh;
        }
        .app-container {
          max-width: 900px;
          margin: 40px auto;
          padding: 20px;
          font-family: Arial;
          transition: background 0.3s, color 0.3s;
        }
        .loader {
          width: 40px;
          height: 40px;
          border: 5px solid #ccc;
          border-top: 5px solid #007bff;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin: 0 auto;
        }
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        .insights-box {
          margin-top: 20px;
          padding: 15px;
          border-radius: 8px;
          background: rgba(240,240,240,0.1);
        }
        .data-table {
          width: 100%;
          border-collapse: collapse;
          background: inherit;
          color: inherit;
        }
        .download-btn {
          padding: 8px 12px;
          cursor: pointer;
          background: #4CAF50;
          color: white;
          border: none;
          border-radius: 6px;
        }
      `}</style>
    </div>
  );
}
